import { createStore } from 'vuex';
import axios from 'axios';

export default createStore({
    state: {
        products: [],
        orders: [],
        users: [],
        authToken: localStorage.getItem('authToken') || null,  // Lê o token do localStorage
    },
    mutations: {
        setProducts(state, products) {
            state.products = products;
        },
        setOrders(state, orders) {
            state.orders = orders;
        },
        setUsers(state, users) {
            state.users = users;
        },
        setAuthToken(state, token) {
            state.authToken = token;
            if (token) {
                localStorage.setItem('authToken', token); // Salva o token no localStorage
            } else {
                localStorage.removeItem('authToken'); // Remove o token do localStorage se for nulo
            }
        },
    },
    actions: {
        async fetchProducts({ commit }) {
            try {
                const response = await axios.get('https://fakestoreapi.com/products');
                commit('setProducts', response.data);
            } catch (error) {
                console.error('Erro ao buscar produtos:', error);
            }
        },
        async fetchOrders({ commit }) {
            try {
                const response = await axios.get('https://fakestoreapi.com/carts');
                commit('setOrders', response.data);
            } catch (error) {
                console.error('Erro ao buscar pedidos:', error);
            }
        },
        async fetchUsers({ commit }) {
            try {
                const response = await axios.get('https://fakestoreapi.com/users');
                commit('setUsers', response.data);
            } catch (error) {
                console.error('Erro ao buscar usuários:', error);
            }
        },
        async login({ commit }, credentials) {
            try {
                const response = await axios.post('https://fakestoreapi.com/auth/login', credentials);
                commit('setAuthToken', response.data.token);  // Armazena o token no Vuex
            } catch (error) {
                console.error('Erro no login:', error);
            }
        },
        logout({ commit }) {
            commit('setAuthToken', null); // Remove o token do Vuex
        },
    },
    getters: {
        isAuthenticated(state) {
            return !!state.authToken;  // Retorna verdadeiro se houver um token
        },
    },
});
