import { createRouter, createWebHistory } from 'vue-router';
import Dashboard from '../views/DashboardPage.vue';
import Products from '../views/ProductList.vue';
import Orders from '../views/OrdersList.vue';
import Users from '../views/UserList.vue';
import Login from '../views/UserLogin.vue'; // Componente de Login

const routes = [
    {
        path: '/',
        name: 'UserLogin', // Rota de login
        component: Login,
    },
    {
        path: '/dashboard',
        name: 'DashboardPage',
        component: Dashboard,
        meta: { requiresAuth: true }, // Requer autenticação
    },
    {
        path: '/products',
        name: 'ProductList',
        component: Products,
        meta: { requiresAuth: true }, // Requer autenticação
    },
    {
        path: '/orders',
        name: 'OrderList',
        component: Orders,
        meta: { requiresAuth: true }, // Requer autenticação
    },
    {
        path: '/users',
        name: 'UserList',
        component: Users,
        meta: { requiresAuth: true }, // Requer autenticação
    },
];

const router = createRouter({
    history: createWebHistory(), // Usando o histórico para navegação sem recarregar a página
    routes, // As rotas configuradas
});

// Proteção de rotas - Garante que as rotas que requerem autenticação não sejam acessadas sem um token
router.beforeEach((to, from, next) => {
    const isAuthenticated = !!localStorage.getItem('authToken');
    if (to.meta.requiresAuth && !isAuthenticated) {
        next({ name: 'userlogin' }); // Garantir que o nome esteja correto
    } else {
        next();
    }
});

export default router;
