import { createRouter, createWebHistory } from 'vue-router';
import Dashboard from '../views/DashboardPage.vue';
import Products from '../views/ProductList.vue';
import Orders from '../views/OrdersList.vue';
import Users from '../views/UserList.vue';
import Login from '../views/UserLogin.vue'; // Componente de Login

const routes = [
    {
        path: '/',
        name: 'UserLogin', // Rota de login
        component: Login,
    },
    {
        path: '/dashboard',
        name: 'DashboardPage',
        component: Dashboard,
        meta: { requiresAuth: true }, // Requer autenticação
    },
    {
        path: '/products',
        name: 'ProductList',
        component: Products,
        meta: { requiresAuth: true }, // Requer autenticação
    },
    {
        path: '/orders',
        name: 'OrderList',
        component: Orders,
        meta: { requiresAuth: true }, // Requer autenticação
    },
    {
        path: '/users',
        name: 'UserList',
        component: Users,
        meta: { requiresAuth: true }, // Requer autenticação
    },
];

const router = createRouter({
    history: createWebHistory(), // Usando o histórico para navegação sem recarregar a página
    routes, // As rotas configuradas
});

// Proteção de rotas - Garante que as rotas que requerem autenticação não sejam acessadas sem um token
router.beforeEach((to, from, next) => {
    const isAuthenticated = !!localStorage.getItem('authToken'); // Verifica se há um token de autenticação armazenado
    console.log('Verificando autenticação:', isAuthenticated);

    if (to.meta.requiresAuth && !isAuthenticated) {
        // Se a rota requer autenticação e o usuário não está autenticado, redireciona para login
        next({ name: 'UserLogin' });
    } else if (to.name === 'UserLogin' && isAuthenticated) {
        // Se o usuário já estiver autenticado, redireciona para o Dashboard
        next({ name: 'DashboardPage' });
    } else {
        next(); // Permite navegação para a rota solicitada
    }
});

export default router;
