import { createRouter, createWebHistory } from 'vue-router';
import Dashboard from '../views/DashboardPage.vue';
import Products from '../views/ProductList.vue';
import Orders from '../views/OrdersList.vue';
import Users from '../views/UserList.vue';
import Login from '../views/UserLogin.vue';

const routes = [
    {
        path: '/',
        name: 'userlogin',
        component: Login, // Tela de login como padrão
    },
    {
        path: '/dashboard',
        name: 'DashboardPage',
        component: Dashboard,
        meta: { requiresAuth: true }, // Necessita autenticação
    },
    {
        path: '/products',
        name: 'ProductList',
        component: Products,
        meta: { requiresAuth: true }, // Necessita autenticação
    },
    {
        path: '/orders',
        name: 'OrderList',
        component: Orders,
        meta: { requiresAuth: true }, // Necessita autenticação
    },
    {
        path: '/users',
        name: 'UserList',
        component: Users,
        meta: { requiresAuth: true }, // Necessita autenticação
    },
];

const router = createRouter({
    history: createWebHistory(),
    routes,
});

// Proteção de rotas
router.beforeEach((to, from, next) => {
    const isAuthenticated = !!localStorage.getItem('authToken'); // Verifica se o token está armazenado

    if (to.meta.requiresAuth && !isAuthenticated) {
        // Redireciona para login se a rota requer autenticação e o usuário não está autenticado
        next({ name: 'UserLogin' });
    } else if (to.name === 'UserLogin' && isAuthenticated) {
        // Redireciona para o Dashboard se o usuário já está autenticado
        next({ name: 'DashboardPage' });
    } else {
        next(); // Permite navegação
    }
});

export default router;
