// main.js
import { createApp } from "vue";
import App from "./app.vue"; // Certifique-se do nome correto do arquivo
import axios from "axios";
import router from "./router/index.js"; // Importa o Vue Router
import store from "./store/index.js";  // Importa o Vuex para gerenciamento de estado
import "./assets/style.css"; // Importa o arquivo de estilo global

const app = createApp(App);

// Configura Axios globalmente
app.config.globalProperties.$axios = axios;

// Usa o Vue Router e Vuex no aplicativo
app.use(router);
app.use(store);

// Monta o aplicativo no elemento com id "app" no index.html
app.mount("#app");
